#nice ul {
  color: rgba(0,0,0,1);
}

#nice ul section {
  margin-top: 5px;
  margin-bottom: 5px;
}

#nice ol {
  color: rgba(0,0,0,1);
}

#nice ol section {
  margin-top: 5px;
  margin-bottom: 5px;
}

#nice blockquote {
  display: block;
  overflow: auto;
  -webkit-overflow-scrolling: touch;
}

#nice blockquote p {
  margin: 0px;
}

#nice .custom-blockquote {
  display: block;
  overflow: auto;
  -webkit-overflow-scrolling: touch;
}

#nice .custom-blockquote p {
  margin: 0px;
}

#nice .table-of-contents a {
  border: none;
  color: rgba(0,0,0,1);
  font-weight: normal;
}

#nice pre.custom code {
  display: -webkit-box;
  font-family: Consolas, Monaco, Menlo, monospace;
  font-size: 12px;
  -webkit-overflow-scrolling: touch;
}
#nice pre.custom code span {
  line-height: 26px;
}

#nice p code {
  word-wrap: break-word;
  font-family: Operator Mono, Consolas, Monaco, Menlo, monospace;
  word-break: break-all;
}
#nice li code {
  word-wrap: break-word;
  font-family: Operator Mono, Consolas, Monaco, Menlo, monospace;
  word-break: break-all;
}

#nice img {
  display: block;
  margin: 0 auto;
  max-width: 100%;
}
#nice figure a {
  border: none;
}
#nice figure a img {
  margin: 0px;
}
#nice figure {
  display:flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
/* 图片链接嵌套 */
#nice figure a {
  display: flex;
  justify-content: center;
  align-items: center;
}

/* 图片链接嵌套，图片解释 */
#nice figure a + figcaption {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-top: -35px;
  background: rgba(0,0,0,0.7);
  color: white;
  line-height: 35px;
  z-index: 20;
}

#nice .table-container {
  overflow-x: auto;
}
#nice .table-container table {
  display: table;
  text-align: left;
}

#nice .table-container table thead tr th {
  padding: 5px 10px;
}
/* 表格最小列宽4个汉字 */
#nice .table-container table thead tr th:nth-of-type(n) {
  min-width: 85px;
}

#nice .table-container table tbody {
  border: 0;
}
/* 表格最小列宽4个汉字 */
#nice .table-container table tbody tr td:nth-of-type(n) {
  min-width:85px;
}
#nice .table-container table tbody tr td {
  padding: 5px 10px;
}

#nice .footnotes .footnote-num {
  display: inline;
  width: 10%; /*神奇，50px就不可以*/
  background: none;
  font-size: 80%;
  font-family: ptima-Regular, Optima, PingFangSC-light, PingFangTC-light, 'PingFang SC', Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
  padding-top: 2px;
}

#nice .footnote-item p {
  display: inline;
  padding: 0px;
  margin: 0px;
  word-break: break-all;
  flex-basis: 0%;
  flex-grow: 1;
}

#nice .block-equation {
  display:block;
  text-align: center;
  overflow: auto;
  display: block;
  -webkit-overflow-scrolling: touch;
}

#nice .block-equation svg {
  max-width: 300% !important;
  -webkit-overflow-scrolling: touch;
}

#nice .imageflow-layer1 {
  border: none;
  overflow: hidden;
}

#nice .imageflow-layer2 {
  display: flex;
  flex-wrap: nowrap;
  overflow-x: scroll;
}

#nice .imageflow-layer3 {
  flex: 0 0 100%;
}
#nice .imageflow-img {
  display: inline-block;
}

#nice .imageflow-caption {
  text-align: center;
  margin: 0px;
  padding: 0px;
}

#nice .column {
  display: flex;
}

#nice .column .column-left {
  text-align: center;
  padding-right: 5px;
  width: 50%;
}

#nice .column .column-right {
  text-align: center;
  padding-left: 5px;
  width: 50%;
}

